import * as google from 'googleapis';
import credentials from '../claves/ort-project-354900-dbaed0423a37.js';




export async function getSheetName() {
    const auth = new google.auth.GoogleAuth({
      credentials,
      scopes: ['https://www.googleapis.com/auth/spreadsheets'],
    });
  
    const sheets = google.sheets({ version: 'v4', auth });
  
    const spreadsheetId = '1MfA7nJsTqfp0qRC1gggtL_kiAfOykRYceANi2txff3U';
    const sheetId = '0';
  
    try {
      const response = await sheets.spreadsheets.get({
        spreadsheetId,
        ranges: [],
        includeGridData: false,
      });
  
      const sheetProperties = response.data.sheets.find(sheet => sheet.properties.sheetId === Number(sheetId)).properties;
      const sheetName = sheetProperties.title;
      console.log('Nombre de la hoja:', sheetName);
    } catch (error) {
      console.error('Error al obtener el nombre de la hoja:', error);
    }
  }
  
  getSheetName();
